import React from 'react'
import App from '../App';
import '../style.css'

const mongo = require('mongodb');
const MongoClient = mongo.MongoClient;
const url = 'mongodb://localhost';

function AppliedCardComp(prop){
  return(
        <div className = "card bg-light mb-3" style = {{width:"25rem"}}>
          <h4 className = "card-header" style = {{display: prop.company ? "block" : "none"}}>{prop.company}</h4>
          <h5 className = "card-title">{prop.title}</h5>
          <p className = "card-text">{prop.desc}</p>
          <div align = "center">
            <button type="button" class="btn btn-secondary" style = {{margin: "5px"}}>Interview</button>
            <button type="button" class="btn btn-success" style = {{margin: "5px"}}>Offer</button>
            <button type="button" class="btn btn-danger" style = {{margin: "5px"}}>Reject</button>
          </div>
        </div>
  )
}


export default AppliedCardComp
