import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import AppliedHeaderComp from './components/AppliedHeaderComp'
import InterviewHeaderComp from './components/InterviewHeaderComp'
import OfferHeaderComp from './components/OfferHeaderComp'
import RejectHeaderComp from './components/RejectHeaderComp'

import AppliedCardComp from './components/AppliedCardComp'
import InterviewCardComp from './components/InterviewCardComp'
import OfferCardComp from './components/OfferCardComp'
import RejectCardComp from './components/RejectCardComp'

import jsonData from './jsonData'

class App extends React.Component{
  constructor(){
    super()
    this.state = {
      stateData: jsonData
    }
  }

  moveTo(){
    console.log("Entered")
  }
  render(){
    const AppliedcompData = this.state.stateData.map(function(data){
        if(data.status == "Applied"){
            return <AppliedCardComp company = {data.lname} title = {data.name} desc = {data.desc}/>
        }
      })

      const InterviewcompData = this.state.stateData.map(function(data){
        if(data.status == "Interview"){
            return <InterviewCardComp company = {data.lname} title = {data.name} desc = {data.desc}/>
        }
      })

      const OffercompData = this.state.stateData.map(function(data){
        if(data.status == "Offer"){
            return <OfferCardComp company = {data.lname} title = {data.name} desc = {data.desc}/>
        }
      })

      const RejectcompData = this.state.stateData.map(function(data){
        if(data.status == "Reject"){
            return <RejectCardComp company = {data.lname} title = {data.name} desc = {data.desc}/>
        }
      })

      return (
      <div>
        <div className = "row">
          <div className = "column" style = {{margin: "4px"}}>
          <AppliedHeaderComp />
          {AppliedcompData}
          </div>

          <div className = "column" style = {{margin: "4px"}}>
          <InterviewHeaderComp/>
          {InterviewcompData}
          </div>

          <div className = "column" style = {{margin: "4px"}}>
          <OfferHeaderComp/>
          {OffercompData}
          </div>

          <div className = "column" style = {{margin: "4px"}}>
          <RejectHeaderComp/>
          {RejectcompData}
          </div>

        </div>
      </div>
      )
    }
  }

export default App;
