import React from 'react'
import App from '../App';
import '../style.css'

const AppliedCardComp = (props) => {
  return(
    <div>
        {
          props.applications.map((item) => (

          <div className = "card bg-light mb-3" style = {{width:"100%"}}>
            <h4 className = "card-header">{item.company}</h4>
            <h5 className = "card-title">{item.title}</h5>
            <p className = "card-text">{item.desc}</p>
              <button type="button" class="btn btn-secondary" key = {i} onClick = {props.updateCurrentApplication.bind(this,item)} style = {{margin: "5px"}}>Interview</button>
              <button type="button" class="btn btn-success" style = {{margin: "5px"}}>Offer</button>
              <button type="button" class="btn btn-danger" style = {{margin: "5px"}}>Reject</button>
          </div>
        ))}
      </div>
  );
}


export default AppliedCardComp
