import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import axios from 'axios'

import AppliedHeaderComp from './components/AppliedHeaderComp'
import InterviewHeaderComp from './components/InterviewHeaderComp'
import OfferHeaderComp from './components/OfferHeaderComp'
import RejectHeaderComp from './components/RejectHeaderComp'

import AppliedCardComp from './components/AppliedCardComp'
import InterviewCardComp from './components/InterviewCardComp'
import OfferCardComp from './components/OfferCardComp'
import RejectCardComp from './components/RejectCardComp'

import jsonData from './jsonData'

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      applications: [],
      currentApp:{},
      stateData: jsonData
    }

    this.updateCurrentApplication = this.updateCurrentApplication.bind(this)
  }

  componentDidMount(){
    const url = 'http://localhost:4000/Application'
    axios.get(url).then((Response) => {
      this.setState({
        applications: Response.data
      })
    })
  }
  updateCurrentApplication(item){
    this.setState({
      currentApp: item
    })

    console.log(this.state.currentApp)
  //   const update = {
  //     company: this.state.currentApp.company,
  //     title: this.state.currentApp.title,
  //     desc: this.state.currentApp.desc,
  //     status: 'Interview'
  //   }
  //   let interviewUrl = 'http://localhost:4000/Application/:'+this.state.currentApp._id
  //   axios.put(interviewUrl,update).then((response) =>{
  //       console.log(response)
  //   }
  // ).catch((error) => {
  //   console.log(error)
  // })
  }

  render(){
      const InterviewcompData = this.state.applications.map(function(data){
        if(data.status == "Interview"){
            return <InterviewCardComp company = {data.company} title = {data.title} desc = {data.desc}/>
        }
      })

      const OffercompData = this.state.applications.map(function(data){
        if(data.status == "Offer"){
            return <OfferCardComp company = {data.company} title = {data.title} desc = {data.desc}/>
        }
      })

      const RejectcompData = this.state.applications.map(function(data){
        if(data.status == "Reject"){
            return <RejectCardComp company = {data.company} title = {data.title} desc = {data.desc}/>
        }
      })

      return (
      <div>
        <div className = "row">
          <div className = "column" style = {{margin: "4px", width: '24%'}}>
          <AppliedHeaderComp />
          <AppliedCardComp applications = {this.state.applications}
            updateCurrentApplication = {this.updateCurrentApplication}/>
          </div>

          <div className = "column" style = {{margin: "4px", width: '24%'}}>
          <InterviewHeaderComp/>
          {InterviewcompData}
          </div>

          <div className = "column" style = {{margin: "4px", width: '24%'}}>
          <OfferHeaderComp/>
          {OffercompData}
          </div>

          <div className = "column" style = {{margin: "4px", width: '24%'}}>
          <RejectHeaderComp/>
          {RejectcompData}
          </div>

        </div>
      </div>
      )
    }
  }

export default App;
